import React, { useState, useEffect, useRef } from "react"; // Fixed import
import Slider from "react-slick";
import Confetti from "react-confetti";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./App.css";

// Image imports
import photo1 from "./asset/img/photo 1.jpg";
import photo5 from "./asset/img/photo 5.jpg";
import photo3 from "./asset/img/photos 3.jpg";
import photo4 from "./asset/img/photo 4.jpg";
import photo6 from "./asset/img/photo 6.jpg";
import photo7 from "./asset/img/photo 7.jpg";
import photo8 from "./asset/img/photo 8.jpg";
import photo9 from "./asset/img/photo 9.jpg";
import photo10 from "./asset/img/photo 10.jpg";
import photo12 from "./asset/img/photo 12.jpg";

const photos = [photo1, photo5, photo3, photo4, photo6, photo7, photo8, photo9, photo10, photo12];

// Birthday set to today, May 7, 2025
const birthdayDate = new Date("2025-05-07T00:00:00");

// Google Drive video link
const birthdayVideo = "https://drive.google.com/uc?export=download&id=1QAiV7QSqrKTp6Iss7bpbV4ibU-Lb8Qi6";

function App() {
  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());
  const [isBirthday, setIsBirthday] = useState(false);
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });
  const [showFireworks, setShowFireworks] = useState(true);
  const [videoError, setVideoError] = useState(null);
  const videoRef = useRef(null);

  function calculateTimeLeft() {
    const now = new Date();
    const difference = birthdayDate - now;

    if (difference <= 0) return null;

    return {
      days: Math.floor(difference / (1000 * 60 * 60 * 24)),
      hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((difference / 1000 / 60) % 60),
      seconds: Math.floor((difference / 1000) % 60),
    };
  }

  useEffect(() => {
    const timer = setInterval(() => {
      const newTimeLeft = calculateTimeLeft();
      setTimeLeft(newTimeLeft);
      setIsBirthday(!newTimeLeft);
    }, 1000);

    const handleResize = () => {
      setWindowSize({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener("resize", handleResize);

    return () => {
      clearInterval(timer);
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  // Stop fireworks after 10 seconds for performance
  useEffect(() => {
    if (isBirthday && showFireworks) {
      const timeout = setTimeout(() => setShowFireworks(false), 10000);
      return () => clearTimeout(timeout);
    }
  }, [isBirthday, showFireworks]);

  // Handle video playback and errors
  useEffect(() => {
    if (isBirthday && videoRef.current) {
      videoRef.current.play().catch((error) => {
        console.error("Video playback failed:", error.message, error);
        setVideoError("Video failed to play. Click 'Try Playing Video' or check the Google Drive link.");
      });
    }
  }, [isBirthday]);

  const handleVideoError = (e) => {
    const error = e.target.error;
    let errorMessage = "Unable to load video. Ensure the Google Drive link is public and the file is a valid MP4.";
    if (error) {
      console.error("Video error details:", {
        code: error.code,
        message: error.message,
        MEDIA_ERR_ABORTED: error.code === 1,
        MEDIA_ERR_NETWORK: error.code === 2,
        MEDIA_ERR_DECODE: error.code === 3,
        MEDIA_ERR_SRC_NOT_SUPPORTED: error.code === 4,
      });
      if (error.code === 4) {
        errorMessage = "Invalid Google Drive link format or sharing settings. Ensure the link is public.";
      } else if (error.code === 3) {
        errorMessage = "Video file is corrupted or cannot be decoded.";
      } else if (error.code === 2) {
        errorMessage = "Network error: Could not load video from Google Drive.";
      } else if (error.code === 1) {
        errorMessage = "Video playback was aborted. Try clicking 'Try Playing Video'.";
      }
    }
    setVideoError(errorMessage);
  };

  const handlePlayButton = () => {
    if (videoRef.current) {
      videoRef.current.play().catch((error) => {
        console.error("Manual video playback failed:", error.message, error);
        setVideoError("Playback failed. Check the Google Drive link or refresh the page.");
      });
    }
  };

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3500,
    arrows: true,
    adaptiveHeight: true,
    lazyLoad: "ondemand",
  };

  return (
    <div className="App">
      {isBirthday && (
        <>
          <Confetti
            width={windowSize.width}
            height={windowSize.height}
            recycle={false}
            numberOfPieces={100}
          />
          {showFireworks && (
            <div className="fireworks-container">
              {Array.from({ length: 8 }).map((_, i) => (
                <div
                  key={i}
                  className="firework-particle"
                  style={{
                    "--delay": `${Math.random() * 0.5 + i * 0.2}s`,
                    "--x-pos": `${Math.random() * 80 + 10}%`,
                    "--y-pos": `${Math.random() * 50 + 10}%`,
                  }}
                />
              ))}
            </div>
          )}
        </>
      )}

      <header className="header">
        <h1>Happy Birthday, Beautiful! üéâ</h1>

        {isBirthday && (
          <button
            className="fireworks-toggle"
            onClick={() => setShowFireworks(!showFireworks)}
            aria-label={showFireworks ? "Hide fireworks" : "Show fireworks"}
          >
            {showFireworks ? "Hide Fireworks" : "Show Fireworks"}
          </button>
        )}

        {!isBirthday && timeLeft ? (
          <div className="countdown">
            <p>Countdown to Your Special Day:</p>
            <div className="timer">
              <div>
                <span>{timeLeft.days}</span>Days
              </div>
              <div>
                <span>{timeLeft.hours}</span>Hours
              </div>
              <div>
                <span>{timeLeft.minutes}</span>Minutes
              </div>
              <div>
                <span>{timeLeft.seconds}</span>Seconds
              </div>
            </div>
          </div>
        ) : (
          <div className="celebration">
            <h2>üéÇ Today is your day! üéÇ</h2>
            {videoError ? (
              <div className="video-error-container">
                <p className="video-error">{videoError}</p>
                <button
                  className="play-button"
                  onClick={handlePlayButton}
                  aria-label="Try playing video"
                >
                  Try Playing Video
                </button>
              </div>
            ) : (
              <video
                ref={videoRef}
                className="birthday-video"
                autoPlay
                loop
                playsInline
                preload="auto"
                aria-label="Birthday celebration video with audio"
                onError={handleVideoError}
              >
                <source src={birthdayVideo} type="video/mp4" />
                <source
                  src="https://videos.pexels.com/video-files/855529/855529-hd_1280_720_24fps.mp4"
                  type="video/mp4"
                />
                Your browser does not support the video tag.
              </video>
            )}
            <p>
              Happy birthday my love as you grow older i want you to know that you are loved
              and i am very proud of you for what youve achieved so far and i pray to God that he can
              be able to give you more strength to even do better. i love youuu so much üíñ
            </p>
          </div>
        )}
      </header>

      <section className="gallery">
        <Slider {...sliderSettings}>
          {photos.map((photo, index) => (
            <div key={index} className="slide">
              <img
                src={photo}
                alt={`Birthday memory ${index + 1}`}
                className="slide-image"
                loading="lazy"
              />
            </div>
          ))}
        </Slider>
      </section>

      <footer>
        <p>Made with ‚ù§Ô∏è just for you.</p>
      </footer>
    </div>
  );
}

export default App;
